import { OkPacket } from 'mysql2/typings/mysql/lib/protocol/packets/OkPacket.js';
import { ResultSetHeader } from 'mysql2/typings/mysql/lib/protocol/packets/ResultSetHeader.js';
import { RowDataPacket } from 'mysql2/typings/mysql/lib/protocol/packets/RowDataPacket.js';

declare type ProcedureCallPacket<
  T = [RowDataPacket[], ResultSetHeader] | ResultSetHeader,
> = T extends RowDataPacket[]
  ? [T, ResultSetHeader]
  : T extends ResultSetHeader | OkPacket
  ? ResultSetHeader
  : [RowDataPacket[], ResultSetHeader] | ResultSetHeader;

export { ProcedureCallPacket };
